





function copyFile(file1,file2) {
    var fs = require("fs");
    var rs = fs.createReadStream(file1);
    var ws = fs.createWriteStream(file2);
    //循环触发  每次读取64
    var count = 0;
    rs.on("data",(chunk)=>{
        ws.write(chunk,(err)=>{
            if (err) {
                console.error(err);
            } else {
                console.log("本次拷贝成功");
            }
        });
    });
    rs.on("end",()=>{
        console.log("数据读取结束");
    })
}
module.exports = {
    CopyFile : copyFile
}