//计算器
function match(a,b,c) {
    if (c == "+") {
        return a + b;
    } else if (c == "-") {
        return a - b;
    } else if (c == "*") {
        return a * b;
    } else if (c == "/") {
        return a / b;
    } else {
        return "输入错误,请重新输入!";
    }
}

//冒泡排序
function bub(array) {
    for(var i = 0; i < array.length - 1; i++) {
        for(var j = 0; j < array.length - 1 - i; j++) {
            if(array[j] > array[j + 1]) {
                var temp = array[j];
                array[j] = array[j + 1];
                array[j + 1] = temp;
            }
        }
    }
    return array;
}

//定义模块留对外接口
module.exports = {
    match : match,
    bub : bub
}